INSERT INTO MailTemplate(id, mailType, content, subject) VALUES(1, 'HEADER', '<html><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8" /><title>Parcel Tracker</title></head><body><div style="font-family: sans-serif, serif, Garamond;height: 475px; border: 3px groove;border-color: #22B14C; width: 500px; margin: 0 auto;"><div style="width: 500px;"><div style="text-align: center"><img src="http://s9.postimg.org/q3t022pyz/logo.png"></img><p style="font-size: medium; margin: 0px"><b>PARCEL TRACKER</b></p><p style="font-size: 12px; margin-bottom: 20px;">Najlepszy system do śledzenia przesyłek</p></div></div>', '');
INSERT INTO MailTemplate(id, mailType, content, subject) VALUES(2, 'FOOTER', '<p><footer style="height: 100px; clear: both; position: relative; z-index: 10; height: 3em; margin-top: 2em; text-align: center; font-size: 10px"><center>parcel-tracker.com - system do śledzenia przesyłek. Wszelkie prawa zastrzezone<p>Developed by Allocer@2015</center></p><footer></p></div></body></html>', '');
INSERT INTO MailTemplate(id, mailType, content, subject) VALUES(3, 'PARCEL_NEW', '<div style="margin: 0 auto; border: 1px groove; border-color: #FFC90E;width: 475px; background-color: #FFC90E "><div style="margin: 0 auto; border: 1px groove; border-top:none;border-color: #22B14C; width: 450px; background-color: #22B14C"><h3 style="text-align: center; margin: 0px">Twoja przesyłka jest już w naszym oddziale!</h3></div><table style="margin: 0 auto; margin-top: 50px; margin-bottom: 50px;"><tr style="line-height: 25px"><td><b>Numer przesyłki:</b></td><td style="text-align: left">${parcelNumber}</td></tr><tr style="line-height: 25px"><td><b>Nazwa nadawcy:</b></td><td> ${parcelSender}</td></tr><tr style="line-height: 25px"><td><b>Nazwa odbiorcy:</b></td><td> ${parcelRecipient}</td></tr><tr style="line-height: 25px"><td><b>Status przesyłki:</b></td><td> ${parcelStatus}</td></tr></table></div>', 'Parcel Tracker - PRZESYŁKA JEST JUŻ W ODDZIALE');
